+.answer-form {
+    margin: 20px;
+    border: var(--regular-line-width) solid var(--primary);
+    border-radius: var(--border-radius-regular);
+    padding: 30px;
+    display: flex;
+    flex-direction: column;
+}
+
+.answer-button {
+    border: var(--regular-line-width) solid var(--secondary);
